import styled from "styled-components";

const TextContainer = styled.div`
  color: #fff;
  display: -ms-flexbox;
  display: flex;
  padding: 6px;
  word-break: break-word;
  -ms-flex-wrap: wrap;
  flex-wrap: wrap;
  -ms-flex-align: center;
  align-items: center;
  min-width: 300px;
  max-width: 550px;
  border-radius: 4px;
  transform: translate(0px, 0px);
`;

export default TextContainer;
