import React from "react";
import {
  Example,
  FlexContainer,
  Label,
  ResetButton,
  Select,
  ShowCode,
  ShowCodeButton,
  SubTitle
} from "../../../Body";
import { useToggle, useDynamicEventHandler } from "../../../Hooks";

const SelectExample = () => {
  const { values, handleChange, resetValue } = useDynamicEventHandler({
    components: ""
  });
  const [showCode, toggleShowCode] = useToggle(false);

  return (
    <>
      <SubTitle>Select</SubTitle>
      <FlexContainer>
        <Example>
          <Label>Select:</Label>
          <Select
            name="components"
            value={values.components}
            placeholder="Select an option..."
            handleChange={handleChange}
            selectOptions={[
              "React.Component",
              "React.PureComponent",
              "React.StatelessComponent",
              "Pure Functions"
            ]}
            width="20%"
          />
          <ResetButton handleClick={resetValue} />
        </Example>
        <ShowCodeButton showCode={showCode} toggleShowCode={toggleShowCode} />
      </FlexContainer>
      <ShowCode showCode={showCode} fileName="Forms/Select.js" />
    </>
  );
};

export default SelectExample;
